import { useCart } from "../../context/CartContext";

function Cart() {
    const { cart } = useCart();
    let itemCount = 0;

    {
        cart?.map((item) => {
            itemCount += item.qty;
        })
    };

    return (
        <div>
            {itemCount === 0 && (<div class="cart-badge"><span id="cart-count"><i className="fas fa-shopping-cart"></i></span></div>)}
            {itemCount > 0 && (
                <span>
                    <i className="fas fa-shopping-cart"></i>
                    <span className="itemCount">
                        <h6>{itemCount}</h6>
                    </span>
                </span>
            )}

        </div>
    )
};

export default Cart;
