import React, { useState } from 'react';

function Poke() {
    const [poke, setPoke] = React.useState([]);

    React.useEffect(() => {
        const url = 'https://pokeapi.co/api/v2/pokemon?limit=1';
        fetch(url).then((response) => {
            if (response.ok) {
                return response.json();
            } else {
                throw response;
            }
        })
            .then((data) => setPoke(data.results))
            .catch((error) => console.log('Se rompio todo'));



    }, [])
    return (
        <div>
            {poke?.map(pokemon => {
                const url_data = pokemon.url;
                fetch(url_data)
                    .then(response => response.json())
                    .then(function (pokeData) {
                       const pokeDatos = pokeData;
                       return(pokeDatos)
                    })
                return (
                    <div>
                        <h3>{pokemon.name}</h3>
                        <p>{pokemon.url}</p>
                        <img src={pokeDatos.sprites.front_default} />
                    </div>
                );
            })}
        </div>
    )
}

export default Poke;
