import React, { useState } from 'react';

function Poke() {
    const [poke, setPoke] = React.useState([]);
    const [pokeImg, setPokeImg] = React.useState([]);

    React.useEffect(()=> {
        const url = 'https://pokeapi.co/api/v2/pokemon/ditto';        
        fetch(url).then((response)=>{
            if (response.ok){
                return response.json();
            }else {
                throw response;
            }
        })
        .then((data) => setPoke(data.forms))
        .then((data) => setPokeImg(data.sprites))
        .catch((error)=> console.log('Se rompio todo'));
    }, [])
    return(
        <div>
            {poke?.map(pokemon => {
                return(
                    <h5>{pokemon.name}</h5>
                );
            })}
            <img src={pokeImg.front_default} />
        </div>
    )
}

export default Poke;
