import { useCart } from "../../context/CartContext";

function Cart() {
    const { cart } = useCart();
    let itemCount = 0;

    {
        cart?.map((item) => {
            itemCount += item.qty;
        })
    };

    return (
        <div>
            {itemCount > 0 && (<span className="fa-stack fa-2x has-badge" data-count={itemCount}>
                <i className="fa fa-circle fa-stack-2x fa-inverse"></i>
                <i className="fa fa-shopping-cart fa-stack-2x red-cart"></i>
            </span>)}
            {itemCount === 0 && (<i className="fa fa-shopping-cart fa-stack-2x red-cart"></i>)}

        </div>
    )
};

export default Cart;
