import { useCart } from "../../context/CartContext";

function Cart() {
    const {cart} = useCart();
    let itemCount = 0;

    {cart?.map((item) => {
        itemCount += item.qty;
    })};

    return (
       <div>
        <a><i className="fas fa-shopping-cart"></i></a>
        {itemCount > 0 && (<span>{itemCount}</span>)}
       </div>
    )
};

export default Cart;
