import { useCart } from "../../context/CartContext";

function Cart() {
    const { cart } = useCart();
    let itemCount = 0;

    {
        cart?.map((item) => {
            itemCount += item.qty;
        })
    };

    return (
        <div>
            {itemCount === 0 && (<div class="cart-badge"><span id="cart-count"><i className="fas fa-shopping-cart"></i></span></div>)}
            {itemCount > 0 && (
                //     <span className="fa-stack fa-2x has-badge" data-count={itemCount} >
                //     <i className="fa fa-circle fa-stack-2x fa-inverse"></i>
                //     <i className="fa fa-shopping-cart fa-stack-2x red-cart"></i>
                // </span>
                <div class="main-wrapper">
                    <div class="card-wrapper">
                        <div class="icon-section">
                            <div class='cart-wrapper'>
                                <div class="cart">
                                    <ion-icon name="cart-outline"></ion-icon>
                                </div>
                                <div class="cart-badge"><span id="cart-count">{itemCount}</span></div>
                            </div>
                        </div>
                    </div>
                </div>

            )}

        </div>
    )
};

export default Cart;
