import * as React from "react";
import Item from "../Item/Item";
import Card from "../Card/Card";
const ItemList = () => {

    const [data, setData] = React.useState([]);
    const [loading, setLoading] = React.useState(false);
    const [error, setError] = React.useState(null);

    React.useEffect(() => {
    //   const url = <Item />;
    const Item= [
        {
          "id": 0,
          "name": "Jabonera",
          "description": "Lorem Ipsum",
          "price": 950,
          "image": "https://drive.google.com/uc?export=view&id=1NJhtDAyveUU9d4tzL2UrdvKJA3QxpMOH"
        },
        {
          "id": 1,
          "name": "Aceitera",
          "description": "Lorem Ipsum",
          "price": 630,
          "image": "https://drive.google.com/uc?export=view&id=1xTiaJhVfG0li0UvH0kjGUEZU2-xASOww"
        },
        {
          "id": 2,
          "name": "Jarron Luna",
          "description": "Lorem Ipsum",
          "price": 720,
          "image": "https://drive.google.com/uc?export=view&id=1JEDOMGVw6Mpx7OfaaYGSP3rbKH9dFVQL"
        },
        ]

      setLoading(true);
      fetch(Item)
        .then((response) => {
          if (response.ok) {
            return response.json();
          } else {
            throw response;
          }
        })
        .then((data) => setData(data))
        .catch((error) => setError(error))
        .finally(() => setLoading(false));
    }, []);
  return (
    <>
      <div style={{ display: "flex", justifyContent: "space-evenly", flexWrap: "wrap" }}>
        {loading && <p>Cargando...</p>}
        {error && (
          <p>
            Ha habido un error: {error.status} {error.statusText}
          </p>
        )}

        {data?.map((item) => {
          return (
            <Card
              key={item.id}
              name={item.name}
              description={item.description}
              image={item.image}
              price={item.price}
            />
          );
        })}
      </div>
    </>
  );
};

export default ItemList;
